import React, { Component } from 'react';
import API from '../../utils/API'

class List extends Component {
    constructor(props) {
      super(props);
      this.state = {
        books: [],
        title: "",
        authors: "",
        description: "",
        image:"",
        link: ""
      }
    }

    loadBooks = () => {
      API.getAllBooks()
        .then(res => console.log(res.data))
        .catch(err => console.log(err))
    }
  
    deleteBook = (id) => {
      console.log(id);
      console.log('delete btn');
    }

    render() {
      return (
        <div >
          <div class="card" >
            <div class="card-body">
              <h5 class="card-title" style={styles.marginLeft}>Saved Books</h5>
              <ul className="list-group"> 
                <li className="list-group-item">
                  <div className="row">
                    <div className="col-lg-4">
                      <h4>{props.title}</h4>
                      
                      <h6>Written by: {props.author}</h6>
                    </div>
                    <div className="col-lg-4"></div>
                    <div className="col-lg-4">
                      <button type='button' className="btn mr-1 btn-dark" onClick={ deleteBook }>Delete</button>
                      <a href={props.link} target="_blank"><button type='button' className="btn mr-1 btn-dark">View</button></a>
                    </div>
                  </div>
                  <div className="row">
                    <div className="col-lg-4"><img src={props.image}></img></div>
                    <div className="col-lg-8"><p>{props.description}</p></div>
                  </div>
                </li>
                <br></br>
              </ul>
            </div>
          </div>
        </div>
      )
    }
}

export default List;
