import React, { Component } from 'react';
import API from '../../utils/API'

class List extends Component {
  constructor(props) {
    this.state = {
      books: [],
      title: "",
      authors: "",
      description: "",
      image: "",
      link: "",
      _id: ""
    }
  }

  componentDidMount() {
    console.log('mounted');
    this.loadBooks();
  };

  loadBooks() {
    API.getAllBooks()
      .then(res => {
        console.log('data', res.data);
        this.setState({ 
          books: res.data, 
          title: res.data.title,
          authors: res.data.author,
          description: res.data.description,
          image: res.data.image,
          link: res.data.link,
          _id: res.data._id
        })
    })
  };
  
  deleteBook = (id) => {
    console.log('delete btn');
    console.log(id);
    API.deleteBook(id)
      .then(res => loadBooks())
      .catch(err => console.log(err))
  }
  render() {
    return (
      <div >
        <div class="card" >
          <div class="card-body">
            <h5 class="card-title" style={styles.marginLeft}>Saved Books</h5>
            <ul className="list-group"> 
              <li className="list-group-item">
                <div className="row">
                  <div className="col-lg-4">
                    <h4>{props.title}</h4>
                    
                    <h6>Written by: {props.author}</h6>
                  </div>
                  <div className="col-lg-4"></div>
                  <div className="col-lg-4">
                    <button type='button' className="btn mr-1 btn-dark" onClick={ () => deleteBook(props.id)  }>Delete</button>
                    <a href={props.link} target="_blank"><button type='button' className="btn mr-1 btn-dark">View</button></a>
                  </div>
                </div>
                <div className="row">
                  <div className="col-lg-4"><img src={props.image}></img></div>
                  <div className="col-lg-8"><p>{props.description}</p></div>
                </div>
              </li>
              <br></br>
            </ul>
          </div>
        </div>
      </div>
      )
    }
}

export default List;
