import React, { Component } from 'react';
import API from '../utils/API'
import Items from '../components/List/SearchItems'

class Search extends Component {
    constructor(props) {
        super(props);
        this.state = {
            books: [],
            title: "",
            authors: "",
            description: "",
            image: "",
            link: ""
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    componentDidMount() {
        this.loadBooks()
        console.log('books', this.state.books)
    };

    loadBooks() {
        API.getBooks(this.state.title)
            .then(res => {
            console.log(res.data);
            this.setState({ 
                books: res.data.items
            })
        })
    };

    handleChange(event){
        const {name, value} = event.target;
        this.setState({
            name: value
        })
    }

    handleSubmit(event){
        event.preventDefault()
        API.getBooks(this.state.value)
        .then(res => this.setState(state =>{
            return {books: res.data.items}
        }))
    }

    render() {
        return (
            <div className="container">
                <h3>Search a Book</h3>
                <input type="text" name='title' value={this.state.title} onChange={this.handleChange} className="form-control" placeholder="Search..." aria-label="Recipient's username" aria-describedby="button-addon2"></input>
                <button className="btn btn-outline-secondary mt-3 mb-3" onClick={this.handleSubmit} type="button" id="button-addon2">Search</button>
                {this.state.books.map(book => 
                    <Items 
                        title={book.volumeInfo.title} 
                        author={book.volumeInfo.authors} 
                        description={book.volumeInfo.description} 
                        link={book.volumeInfo.infoLink} 
                        image={book.volumeInfo.imageLinks.smallThumbnail} 
                    />  
            )}           
        </div>
  )
    }
}

export default Search;
