import React from 'react';

function Search (props) {
    this.state = {value: ''};
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

    function handleChange(event){
        this.setState({value: event.target.value})
    }



    return (
    <div class="container">
        <h3>Search a Book</h3>
        <input type="text" value={this.state.value} onChange={this.handleChange} class="form-control" placeholder="Search..." aria-label="Recipient's username" aria-describedby="button-addon2"></input>
        <button class="btn btn-outline-secondary mt-3" onClick={this.handleSubmit} type="button" id="button-addon2">Search</button>
    </div>
  )
}

export default Search;
